    @{ Url = "https://readdy.ai/api/search-image?query=Professional%20headshot%20of%20a%20male%20neuroscientist%20with%20glasses%2C%20wearing%20a%20button-up%20shirt%2C%20against%20a%20neutral%20background.%20The%20image%20should%20convey%20intelligence%20and%20professionalism%2C%20with%20soft%20lighting%20highlighting%20his%20facial%20features.&width=200&height=200&seq=123458&orientation=squarish"; Filename = "rafael-costa.jpg"; },
    @{ Url = "https://readdy.ai/api/search-image?query=Professional%20headshot%20of%20a%20female%20educational%20psychologist%20with%20a%20friendly%20expression%2C%20wearing%20professional%20attire%2C%20against%20a%20neutral%20background.%20The%20image%20should%20convey%20expertise%20and%20warmth%2C%20with%20soft%20lighting%20highlighting%20her%20facial%20features.&width=200&height=200&seq=123459&orientation=squarish"; Filename = "camila-oliveira.jpg"; },
    @{ Url = "https://readdy.ai/api/search-image?query=Professional%20headshot%20of%20a%20male%20technology%20specialist%20with%20a%20confident%20smile%2C%20wearing%20a%20casual%20professional%20outfit%2C%20against%20a%20neutral%20background.%20The%20image%20should%20convey%20innovation%20and%20technical%20expertise%2C%20with%20soft%20lighting%20highlighting%20his%20facial%20features.&width=200&height=200&seq=123460&orientation=squarish"; Filename = "andre-martins.jpg"; },
    @{ Url = "https://readdy.ai/api/search-image?query=A%20modern%2C%20clean%20user%20interface%20of%20a%20mental%20health%20assessment%20platform%20for%20children%20and%20teenagers.%20The%20screen%20displays%20colorful%2C%20engaging%20assessment%20modules%20with%20intuitive%20navigation.%20The%20interface%20features%20soft%20pastel%20colors%2C%20primarily%20light%20blue%20and%20white%2C%20with%20clear%20typography%20and%20friendly%20icons.%20The%20design%20is%20professional%20yet%20approachable%2C%20showing%20data%20visualization%20elements%20like%20charts%20and%20progress%20indicators.&width=600&height=400&seq=123461&orientation=landscape"; Filename = "platform-interface.jpg"; },
    @{ Url = "https://readdy.ai/api/search-image?query=A%20professional%20scientific%20research%20setting%20showing%20researchers%20analyzing%20mental%20health%20data%20on%20computers.%20The%20scene%20includes%20charts%2C%20graphs%2C%20and%20scientific%20equipment%20in%20a%20modern%20laboratory%20environment.%20The%20image%20has%20a%20clean%2C%20professional%20aesthetic%20with%20soft%20lighting%20and%20a%20color%20palette%20featuring%20light%20blue%20and%20white%20tones.&width=600&height=400&seq=123462&orientation=landscape"; Filename = "scientific-research.jpg"; },
    @{ Url = "https://readdy.ai/api/search-image?query=Professional%20headshot%20of%20a%20female%20school%20principal%20with%20a%20warm%20smile%2C%20wearing%20professional%20attire%2C%20against%20a%20neutral%20background.%20The%20image%20should%20convey%20leadership%20and%20approachability%2C%20with%20soft%20lighting%20highlighting%20her%20facial%20features.&width=100&height=100&seq=123463&orientation=squarish"; Filename = "fernanda-almeida.jpg"; },
    @{ Url = "https://readdy.ai/api/search-image?query=Professional%20headshot%20of%20a%20middle-aged%20father%20with%20a%20friendly%20expression%2C%20wearing%20casual%20attire%2C%20against%20a%20neutral%20background.%20The%20image%20should%20convey%20warmth%20and%20reliability%2C%20with%20soft%20lighting%20highlighting%20his%20facial%20features.&width=100&height=100&seq=123464&orientation=squarish"; Filename = "ricardo-souza.jpg"; }
)

Write-Host "Iniciando download das imagens..." -ForegroundColor Green

foreach ($image in $imageUrls) {
    $outputPath = "img/$($image.Filename)"
    Write-Host "Baixando $($image.Filename)..." -ForegroundColor Yellow
    Invoke-WebRequest -Uri $image.Url -OutFile $outputPath -UseBasicParsing
    Write-Host "âœ“ $($image.Filename) baixado com sucesso!" -ForegroundColor Green
}

Write-Host "Download concluido!" -ForegroundColor Green 